        DTSMake.prototype.checkReplaceType = function (s) {
            var rt;
            if (s === "!ret") {
                //return
                rt = ReplaceType.RETURN;
            }
            else if (/^![0-9]+/.test(s)) {
                //param
                rt = ReplaceType.PARAM;
            }
            else if (/^[A-Z].*/.test(s)) {
                //class or object, may be class
                rt = ReplaceType.CLASS;
            }
            else if (/^<.+>$/.test(s)) {
                //array type
                rt = ReplaceType.ARRAY;
                //TODO:Array type replace
            }
            else {
                //other
                rt = ReplaceType.OTHER;
            }
            return rt;
        };
